import React, { useState } from 'react'
import { Divider, Image, Tooltip } from 'antd';
import Modal from 'react-modal';
import DataCancionText from './Data/DataCancionText';

const CancionText = () => {

    const Historias = DataCancionText();
   

    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedSong, setSelectedSong] = useState('');


    const openModal = (cancion) => {
        setModalIsOpen(true);
        console.log(cancion)
        setSelectedSong(cancion);
    };

    const closeModal = () => {
        setModalIsOpen(false);
        setSelectedSong('');

    };

    return (
        <>

            <div className='min-h-screen flex flex-col p-2 gap-3 '>
                {Historias.map((historia) => (
                    <div key={historia.id} className=' p-2 h-80  rounded flex flex-col gap-2 shadow-lg'>
                        <div className='flex justify-between'>
                            <h3 className='text-2xl'>{historia.titulo}</h3>
                            <Tooltip placement="left" title="Reproducir Cancion">
                                <button onClick={() => openModal(historia.cancion)} className='text-white shadow-lg bg-red-500 rounded-full p-2 flex items-center '>
                                    <ion-icon name="play"></ion-icon>
                                </button>
                            </Tooltip>

                        </div>
                        <hr className=' p-1 rounded bg-blue-300' />
                        <div className='flex flex-row gap-4 '>
                            <div>
                                <Image
                                    width={240}
                                    height={220}
                                    className='rounded'
                                    src={historia.image}
                                    fallback="data:image/png;base64,"
                                />
                            </div>
                            <div className='h-64 overflow-auto'>
                                <p>{historia.texto}</p>
                            </div>

                        </div>
                    </div>
                ))}

            </div>

            <div>

                <Modal
                    isOpen={modalIsOpen}
                    onRequestClose={closeModal}
                    contentLabel="Video Modal"
                    className='w-full h-full flex items-center justify-center '
                >
                    <div className='p-4 bg-white shadow-lg rounded border-2 border-pink-300' style={{ width: '80%' }}>
                        <button onClick={closeModal} className='mb-4 bg-pink-300 flex p-2 text-2xl rounded-full text-white text-semibold'>
                            <ion-icon name="close-outline"></ion-icon>
                        </button>

                        <iframe
                            src={`https://open.spotify.com/embed/track/${selectedSong}?utm_source=generator`}
                            className='w-full'
                            height="352"
                            allowfullscreen=""
                            allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
                            loading="lazy"
                        ></iframe>



                    </div>
                </Modal>

            </div>
        </>
    )
}

export default CancionText